import './App.css';
import React, { useReducer, useRef } from 'react';
import {BrowserRouter, Route, Routes} from 'react-router-dom'
import ReviewList from './pages/ReviewList'
import New from './pages/New'
import Edit from './pages/Edit'
import MyReview from './pages/MyReview'

//Components
import MyButton from './components/MyButton';
import MyHeader from './components/MyHeader';

const reducer = (state, action) => {
  let newState = [];
  switch (action.type){
    case 'INIT' : {
      return action.data;
    }
    case 'CREATE': {
      const newItem= {
        ...action.data
      };
      newState = [newItem, ...state];
      break;
    }
    case 'REMOVE': {
      newState = state.filter((it)=>it.id !== action.targetId);
      break;
    }
    case 'EDIT': {
      newState = state.map((it)=> it.id === action.data.id? {...action.data}: it)
      break;
    }
    default:
      return state;
  }
  return newState;
};

export const ReviewStateContext = React.createContext();
export const ReviewDispatchContext = React.createContext();

const dummyData = [
  {
    id: "마라왕김탁구",
    score: 5,
    content: "존맛탱탱구리 여기 마라 개맛잇음",
    store: "부천마라 1호점",
    picture: './qFWfOHBd0mx7NmNquwtaSbUjnPumXpk5oi1jxNKpWUsv_eGJe44xm9AePkbhQ6hIxTjMtroFaOFPbhBy0MSbNQ.png',
    date: 1638969241492,
  },
  {
    id: "마라천국",
    score: 5,
    content: "존맛탱탱구리 여기 마라 개맛잇음",
    store: "부천마라 1호점",
    picture: './qFWfOHBd0mx7NmNquwtaSbUjnPumXpk5oi1jxNKpWUsv_eGJe44xm9AePkbhQ6hIxTjMtroFaOFPbhBy0MSbNQ.png',
    date: 1638969241494,
  },
]

function App() {
  const [data, dispatch] = useReducer(reducer, [dummyData]);
  const dataId= useRef(0); 

  // CREATE
  const onCreate = (date, content, score, store, picture) => {
    dispatch({type:"CREATE", data: {
      id: dataId.current,
      date: new Date(date).getTime(),
      content,
      score,
      store,
      picture,
    },
  });
    dataId.current += 1;

  };

  // REMOVE
  const onRemove = (targetId) => {
    dispatch({type:"REMOVE", targetId});
  }

  //EDIT
  const onEdit = (targetId, date, content, score, store, picture) => {
    dispatch({
      type: "EDIT",
      data: {
        id: targetId,
        date: new Date(date).getTime(),
        content,
        score,
        store,
        picture,
      },
    });
  };

  return (
    <ReviewStateContext.Provider value= {data}>
      <ReviewDispatchContext.Provider value={{
        onCreate, onEdit, onRemove,
      }}>
    <BrowserRouter>
    <div className="App">
      <Routes>
        <Route path='/ReviewList' element={<ReviewList/>}/>
        <Route path='/New' element={<New/>}/>
        <Route path='/Edit' element={<Edit/>}/>
        <Route path='/MyReview/:id' element={<MyReview/>}/>
      </Routes>
    </div>
    </BrowserRouter>
    </ReviewDispatchContext.Provider>
    </ReviewStateContext.Provider>
  );
}

export default App;
